mapper.select:
select count(*)
from member
where username = #{username}

select count(*)
from member
where nickname = #{nickname}

select count(*)
from member
where email = #{email}

select member.member_id, username, password, nickname, email
from member
where member.member_id = #{memberId}

mapper.update:
update member





        username = #{username},



        password = #{password},



        nickname = #{nickname},



        email = #{email}



where member_id = #{memberId}
