mapper.select:
select *
from pins
order by p_id desc limit #{amount} offset #{offset}

select *
from pins
where member_id = #{mId}
order by p_id desc limit #{amount} offset #{offset}

mapper.update:
update pins
set tag=#{tag},
    image_path=#{image_path}
where p_id = #{pId} and member_id = #{mId}

mapper.delete:
delete
from pins
where p_id = #{pid} and member_id = #{mid}
