mapper.select:
SELECT pins.p_id, member.nick, pins.img_path, pins.tag, pins.n_tag, pins.width, pins.height, pins.seed
FROM pins
INNER JOIN member ON pins.m_id = member.m_id
WHERE member.m_id = #{mId}
ORDER BY pins.p_id DESC
LIMIT #{amount} OFFSET #{offset}

mapper.update:
update pins
set tag=#{tag},
    img_path=#{imagePath}
where p_id = #{pId} and pins.m_id = #{mId}

mapper.delete:
delete
from pins
where p_id = #{pid} and pins.m_id = #{mid}
