mapper.select:
SELECT pins.p_id, member.nick, pins.img_path, pins.tag, pins.n_tag, pins.width, pins.height, pins.seed
FROM pins
INNER JOIN member ON pins.m_id = member.m_id
WHERE member.m_id = #{memberId}
ORDER BY pins.p_id DESC
LIMIT #{amount} OFFSET #{offset}

SELECT pins.p_id, pins.img_path, pins.tag, pins.n_tag, pins.width, pins.height, pins.seed
FROM pins
WHERE pins.p_id = #{pinId}

mapper.delete:
delete
from pins
where p_id = #{pinId} and pins.m_id = #{memberId}
