mapper.select:
select *
from pins
order by pId desc limit #{amount} offset #{offset}

select *
from pins
where mId = #{mId}
order by pId desc limit #{amount} offset #{offset}

mapper.update:
update pins
set tag=#{tag},
    imagePath=#{imagePath}
where pId = #{pId} and mId = #{mId}

mapper.delete:
delete
from pins
where pId = #{pid} and mId = #{mid}
