mapper.select:
SELECT pins.pin_id, pins.member_id, member.nickname, pins.img_path, pins.prompt, pins.negative_prompt, pins.width, pins.height, pins.seed FROM pins INNER JOIN member ON pins.member_id = member.member_id
AND pins.prompt LIKE CONCAT('%', #{keyword}, '%')
ORDER BY pins.pin_id DESC LIMIT #{amount} OFFSET #{offset}

mapper.delete:
delete
from pins
where pin_id = #{pinId}
  and pins.member_id = #{memberId}
