mapper.select:
select *
from pins
order by pins.p_id desc limit #{amount} offset #{offset}

select *
from pins
where pins.m_id = #{mId}
order by p_id desc limit #{amount} offset #{offset}

mapper.update:
update pins
set tag=#{tag},
    img_path=#{imagePath}
where p_id = #{pId} and pins.m_id = #{mId}

mapper.delete:
delete
from pins
where p_id = #{pid} and pins.m_id = #{mid}
